package base.staticfinal;public class Count2 {	public static void prt(String s) {		System.out.println(s);	}	Valuee v = new Valuee(10);	static Valuee v1, v2;	static {		prt("v1.c=" + v1.c + "  v2.c=" + v2.c);		v1 = new Valuee(27);		prt("v1.c=" + v1.c + "  v2.c=" + v2.c);		v2 = new Valuee(15);		prt("v1.c=" + v1.c + "  v2.c=" + v2.c);	}	public static void main(String[] args) {		Count2 ct = new Count2();		prt("ct.c=" + ct.v.c);		System.out.println("-----1------");		prt("v1.c=" + v1.c + "  v2.c=" + v2.c);		System.out.println("-----2------");		v1.inc();			prt("v1.c=" + v1.c + "  v2.c=" + v2.c);		System.out.println("-----3------");		prt("ct.c=" + ct.v.c);	}}class Valuee {	static int c = 0;	Valuee() {		c = 15;	}	Valuee(int i) {		c = i;	}	static void inc() {		c++;	}}